import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import Home from '../components/informacion';
import Nosotros from '../components/nosotros';
import Proyectos from '../components/proyectos';
import './cs.css';
import Contactos from '../components/contactos';
import Donaciones from '../components/donaciones';

const Menu = () => {
  const [menuActive, setMenuActive] = useState(false);
  const [isNavFixed, setIsNavFixed] = useState(false);
  const navigate = useNavigate();

  const toggleMenu = () => {
    setMenuActive(!menuActive);
  };

  const handleLinkClick = (path) => {
    setMenuActive(false);
    navigate(path);
  };

  useEffect(() => {
    const handleScroll = () => {
      const nav = document.querySelector('.menu');
      const navPosition = nav.getBoundingClientRect().top;
  
      if (navPosition <= 0) {
        setIsNavFixed(true);
      } else {
        setIsNavFixed(false);
      }
    };
  
    window.addEventListener('scroll', handleScroll);
  
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div>
        <nav className={`menu ${menuActive || isNavFixed ? 'active' : ''}`}>
          <div className="hamburger-icon" onClick={toggleMenu}>
            &#9776;
          </div>
          <ul>
          <li onClick={() => handleLinkClick('/')} >
                            <Link to="/">Informacion</Link>
                        </li>
                        <li onClick={() => handleLinkClick('/nosotros')}>
                            <Link to="/nosotros">Acerca de nosotros</Link>
                        </li>

                        <li onClick={() => handleLinkClick('/proyectos')} >
                            <Link to="/proyectos">Proyectos</Link>
                        </li>
                        <li onClick={() => handleLinkClick('/contactos')}>
                            <Link to='/contactos'>Contactos</Link>                            
                         </li>
                        <li onClick={() => handleLinkClick('/donaciones')}>
                            <Link to= '/donaciones'>Donaciones</Link>
                        </li>
          </ul>
        </nav>
      </div>
      <div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/nosotros" element={<Nosotros />} />
          <Route path="/proyectos" element={<Proyectos />} />
          <Route path="/contactos" element={<Contactos />} />
          <Route path="/donaciones" element={<Donaciones />} />
        </Routes>
      </div>
    </>
  );
};

export default Menu;
